(function ($) {
    var $form = $("form");
    if ($form[0] != undefined) {
        $form.on("click", "a:not(.skip)", function (e) { e.preventDefault(); $form.submit() });
        $form.on("keyup", "input:visible", function (e) { if (e.which == 13) $form.submit() });
        $form.find("input:visible:first").focus();
    }
})(jQuery);